<script>
export default {
    name: 'Floats',
    data() {
        return {
            ui: require('@/assets/images/ui.svg'),
            ux: require('@/assets/images/ux.svg'),
            prod: require('@/assets/images/prod_delight.svg'),
            count: true,
            counter: 0,
            interset: false,
        }
    },
    created() {
        this.timer()
    },
    methods: {
        timer() {
            setInterval(() => {
                if(this.count) {
                    if(this.counter < 360) {
                        this.counter++
                        if(this.counter == 270) {
                            this.intersetOn()
                        }
                    }else {
                        this.counter = 0
                        this.counter++
                    }
                }
            }, 40)
        },
        intersetOn() {
            this.pauseRotate()
            this.interset = true
            // setTimeout(() => {
            //     this.resumeRotate()
            // }, 5000)
        },
        pauseRotate() {
            this.count = false
        },
        resumeRotate() {
            this.count = true
            this.interset = false
        }
    }
}
</script>
